document.addEventListener("click",t=>{const o=t.target.matches("[data-dropdown-btn]");if(!o&&null!==t.target.closest("[data-dropdown]"))return;let d;o&&(d=t.target.closest("[data-dropdown]")).classList.toggle("active-dropdown"),document.querySelectorAll("[data-dropdown].active-dropdown").forEach(t=>{t!==d&&t.classList.remove("active-dropdown")})});